import{_ as t,o as d,c as e,Q as r}from"./chunks/framework.aaef0e9b.js";const W=JSON.parse('{"title":"Windows 11 快捷键","description":"","frontmatter":{},"headers":[],"relativePath":"other/Windows系统/Windows 11 快捷键.md","lastUpdated":1685333320000}'),l={name:"other/Windows系统/Windows 11 快捷键.md"},n=r('<h1 id="windows-11-快捷键" tabindex="-1">Windows 11 快捷键 <a class="header-anchor" href="#windows-11-快捷键" aria-label="Permalink to &quot;Windows 11 快捷键&quot;">​</a></h1><h2 id="常用的系统快捷键大全" tabindex="-1">常用的系统快捷键大全 <a class="header-anchor" href="#常用的系统快捷键大全" aria-label="Permalink to &quot;常用的系统快捷键大全&quot;">​</a></h2><table><thead><tr><th>快捷键</th><th>功能</th></tr></thead><tbody><tr><td>Ctrl + X</td><td>剪切选定项。</td></tr><tr><td>Ctrl + C（或 Ctrl + Insert）</td><td>复制选定项。</td></tr><tr><td>Ctrl + V（或 Shift + Insert）</td><td>粘贴选定项。</td></tr><tr><td>Ctrl + Z</td><td>撤消操作。</td></tr><tr><td>Alt + Tab</td><td>在打开的应用之间切换。</td></tr><tr><td>Alt + F4</td><td>关闭活动项，或者退出活动应用。</td></tr><tr><td>Win + L</td><td>锁定你的电脑。</td></tr><tr><td>Win+ D</td><td>显示和隐藏桌面。</td></tr><tr><td>F2</td><td>重命名所选项目。</td></tr><tr><td>F3</td><td>在文件资源管理器中搜索文件或文件夹。</td></tr><tr><td>F4</td><td>在文件资源管理器中显示地址栏列表。</td></tr><tr><td>F5</td><td>刷新活动窗口。</td></tr><tr><td>F6</td><td>循环浏览窗口中或桌面上的屏幕元素。</td></tr><tr><td>F10</td><td>激活活动应用中的菜单栏。</td></tr><tr><td>Alt + F8</td><td>在登录屏幕上显示你的密码。</td></tr><tr><td>Alt + Esc</td><td>按项目打开顺序循环浏览。</td></tr><tr><td>Alt + 带下划线的字母</td><td>执行该字母相关的命令。</td></tr><tr><td>Alt + Enter</td><td>显示所选项目的属性。</td></tr><tr><td>Alt + 空格键</td><td>打开活动窗口的快捷菜单。</td></tr><tr><td>Alt + 向左键</td><td>返回。</td></tr><tr><td>Alt + 向右键</td><td>前进。</td></tr><tr><td>Alt + Page Up</td><td>向上移动一个屏幕。</td></tr><tr><td>Alt + Page Down</td><td>向下移动一个屏幕。</td></tr><tr><td>Ctrl + F4</td><td>关闭活动文档（在可全屏显示并允许你同时打开多个文档的应用中）。</td></tr><tr><td>Ctrl + A</td><td>选择文档或窗口中的所有项目。</td></tr><tr><td>Ctrl + D（或 Delete）</td><td>删除选定项，将其移至回收站。</td></tr><tr><td>Ctrl + E</td><td>打开搜索（在大多数应用中）。</td></tr><tr><td>Ctrl + R（或 F5）</td><td>刷新活动窗口。</td></tr><tr><td>Ctrl + Y</td><td>恢复操作。</td></tr><tr><td>Ctrl + 向右键</td><td>将光标移动到下一个字词的起始处。</td></tr><tr><td>Ctrl + 向左键</td><td>将光标移动到上一个字词的起始处。</td></tr><tr><td>Ctrl + 向下键</td><td>将光标移动到下一段落的起始处。</td></tr><tr><td>Ctrl + 向上键</td><td>将光标移动到上一段落的起始处。</td></tr><tr><td>Ctrl + Alt + Tab</td><td>使用箭头键在所有打开的应用之间进行切换。</td></tr><tr><td>Alt + Shift + 箭头键</td><td>当组或磁贴的焦点放在“开始”菜单上时，可将其朝指定方向移动。</td></tr><tr><td>Ctrl + Shift + 箭头键</td><td>当磁贴的焦点放在“开始”菜单上时，将其移到另一个磁贴即可创建一个文件夹。</td></tr><tr><td>Ctrl + 箭头键</td><td>打开“开始”菜单后调整其大小。</td></tr><tr><td>Ctrl + 箭头键（移至某个项目）+ 空格键</td><td>选择窗口中或桌面上的多个单独项目。</td></tr><tr><td>Ctrl + Shift（及箭头键）</td><td>选择文本块。</td></tr><tr><td>Ctrl + Esc</td><td>打开“开始”菜单。</td></tr><tr><td>Ctrl + Shift + Esc</td><td>打开任务管理器。</td></tr><tr><td>Ctrl + Shift</td><td>如果多种键盘布局可用，则可切换键盘布局。</td></tr><tr><td>Ctrl + 空格键</td><td>打开或关闭中文输入法编辑器 (IME)。</td></tr><tr><td>Shift + F10</td><td>显示选定项的快捷菜单。</td></tr><tr><td>按 Shift 与任何箭头键</td><td>在窗口中或桌面上选择多个项目，或在文档中选择文本。</td></tr><tr><td>Shift + Delete</td><td>删除选定项，无需先移动到回收站。</td></tr><tr><td>向右键</td><td>打开右侧的下一个菜单，或打开子菜单。</td></tr><tr><td>向左键</td><td>打开左侧的下一个菜单，或关闭子菜单。</td></tr><tr><td>Esc</td><td>停止或离开当前任务。</td></tr><tr><td>PrtScn</td><td>捕获整个屏幕的屏幕截图并将其复制到剪贴板。 注意 你可以更改此快捷方式，以便它还可以打开屏幕截取，这允许你编辑屏幕截图。 依次选择“开始” &gt; “设置” &gt; “轻松访问” &gt; “键盘”，然后打开“Print Screen 快捷方式”下的切换开关。 使用 PrtScn 键打开屏幕截取</td></tr></tbody></table><h2 id="windows徽标键快捷键大全" tabindex="-1">Windows徽标键快捷键大全 <a class="header-anchor" href="#windows徽标键快捷键大全" aria-label="Permalink to &quot;Windows徽标键快捷键大全&quot;">​</a></h2><table><thead><tr><th>快捷键</th><th>功能</th></tr></thead><tbody><tr><td>Win</td><td>打开或关闭“开始”菜单。</td></tr><tr><td>Win + A</td><td>打开快速设置。</td></tr><tr><td>Win + B</td><td>将焦点设置为显示隐藏图标下拉菜单。</td></tr><tr><td>Win+ C</td><td>从 Microsoft Teams 打开聊天。</td></tr><tr><td>Win+ Shift + C</td><td>打开超级按钮菜单。</td></tr><tr><td>Win + Ctrl + C</td><td>打开颜色筛选器（在颜色筛选器设置中首先启用此快捷方式）。</td></tr><tr><td>Win + D</td><td>显示和隐藏桌面。</td></tr><tr><td>Win + E</td><td>打开文件资源管理器。</td></tr><tr><td>Win + F</td><td>打开反馈中心并获取屏幕截图。</td></tr><tr><td>Win + G</td><td>打开 Xbox Game Bar（当游戏处于打开状态时）。</td></tr><tr><td>Win + H</td><td>启动语音键入。</td></tr><tr><td>Win + I</td><td>打开设置。</td></tr><tr><td>Win+ J</td><td>请将焦点设置到可用的 Windows 提示。 当出现 Windows 提示时，请将焦点移到提示上。 再次按下键盘快捷方式，将焦点放在屏幕上 Windows 提示所固定的元素上。</td></tr><tr><td>Win + K</td><td>从“快速设置”打开“投放”。</td></tr><tr><td>Win + L</td><td>锁定你的电脑或切换帐户。</td></tr><tr><td>Win + M</td><td>最小化所有窗口。</td></tr><tr><td>Win + Shift + M</td><td>还原桌面上的最小化窗口。</td></tr><tr><td>Win + N</td><td>打开通知中心和日历。</td></tr><tr><td>Win + O</td><td>锁定设备方向。</td></tr><tr><td>Win + P</td><td>选择演示显示模式。</td></tr><tr><td>Win + Ctrl + Q</td><td>打开快速助手。</td></tr><tr><td>Win + R</td><td>打开“运行”对话框。</td></tr><tr><td>Win + Alt + R</td><td>在焦点中录制游戏窗口的视频（使用 Xbox Game Bar）。</td></tr><tr><td>Win + S</td><td>打开搜素。</td></tr><tr><td>Win + Shift + S</td><td>获取部分屏幕的屏幕截图。</td></tr><tr><td>Win + T</td><td>循环浏览任务栏上的应用。</td></tr><tr><td>Win + U</td><td>打开辅助功能设置。</td></tr><tr><td>Win + V</td><td>打开剪贴板历史记录。 注意 如果不想看到此快捷方式的体式，请依次选择“开始”&gt; “设置”&gt; “系统”&gt; “剪贴板”，然后打开“剪贴板历史记录”下的切换开关。</td></tr><tr><td>Win + Shift + V</td><td>将焦点设置到通知。</td></tr><tr><td>Win + W</td><td>打开小组件。</td></tr><tr><td>Win + X</td><td>打开“快速链接”菜单。</td></tr><tr><td>Win + Y</td><td>在 Windows Mixed Reality 与桌面之间切换输入。</td></tr><tr><td>Win + Z</td><td>打开对齐布局。</td></tr><tr><td>Win + 句号 (.) 或分号 (😉</td><td>打开表情符号面板。</td></tr><tr><td>Win+ 逗号 (,)</td><td>临时速览桌面。</td></tr><tr><td>Win + Pause 键</td><td>显示“系统属性”对话框。</td></tr><tr><td>Win + Ctrl + F</td><td>搜索电脑（如果已连接到网络）。</td></tr><tr><td>Win + 数字</td><td>打开桌面，然后启动固定到任务栏的应用（位于数字所指明的位置）。 如果应用已处于运行状态，则切换至该应用。</td></tr><tr><td>Win + Shift + 数字</td><td>打开桌面，然后启动固定到任务栏的应用新实例（位于数字所指明的位置）。</td></tr><tr><td>Win+ Ctrl + 数字</td><td>打开桌面，然后切换至固定到任务栏的应用的最后活动窗口（位于数字所指明的位置）。</td></tr><tr><td>Win + Alt + 数字</td><td>打开桌面，然后打开固定到任务栏的应用的“跳转列表”（位于数字所指明的位置）。</td></tr><tr><td>Win + Ctrl + Shift + 数字</td><td>打开桌面，然后以管理员身份打开位于任务栏上指定位置的应用新实例。</td></tr><tr><td>Win + Tab</td><td>打开任务视图。</td></tr><tr><td>Win + 向上键</td><td>最大化窗口。</td></tr><tr><td>Win + Alt + 向上键</td><td>将焦点对齐到屏幕上半部分（ 使用 Windows 11）的对齐窗口。</td></tr><tr><td>Win + 向下键</td><td>删除屏幕上的当前应用并最小化桌面窗口。</td></tr><tr><td>Win + Alt + 向下键</td><td>将焦点对齐到屏幕下半部分（ 使用 Windows 11）的对齐窗口。</td></tr><tr><td>Win + 向左键</td><td>最大化屏幕左侧的应用或桌面窗口。</td></tr><tr><td>Win + 向右键</td><td>最大化屏幕右侧的应用或桌面窗口。</td></tr><tr><td>Win+ Home</td><td>最小化活动桌面窗口之外的所有窗口（在第二个笔划时还原所有窗口）。</td></tr><tr><td>Win+ Shift + 向上键</td><td>将桌面窗口拉伸至屏幕顶部和底部。</td></tr><tr><td>Win+ Shift + 向下键</td><td>在垂直方向上还原/最小化活动桌面窗口，而宽度保持不变。</td></tr><tr><td>Win+ Shift + 向左键或向右键</td><td>将桌面上的应用或窗口从一台显示器移动至另一台显示器。</td></tr><tr><td>Win + Shift + 空格键</td><td>在语言和键盘布局中向后循环。</td></tr><tr><td>Win + 空格键</td><td>切换输入语言和键盘布局。</td></tr><tr><td>Win + Ctrl + 空格键</td><td>对之前选择的输入所做的更改。</td></tr><tr><td>Win+ Ctrl + Enter</td><td>打开“讲述人”。</td></tr><tr><td>Win + 加号 (+)</td><td>打开放大镜并放大。</td></tr><tr><td>Win + 减号 (-)</td><td>缩小放大镜。</td></tr><tr><td>Win + Esc</td><td>关闭放大镜。</td></tr><tr><td>Win + 正斜杠 (/)</td><td>开始输入法复原流程。</td></tr><tr><td>Win + Ctrl + Shift + B</td><td>从空白或黑屏唤醒电脑。</td></tr><tr><td>Win + PrtScn</td><td>将全屏屏幕截图保存到文件。</td></tr><tr><td>Win + Alt + PrtScn</td><td>将焦点中的游戏窗口的屏幕截图保存到文件（使用 Xbox Game Bar）。</td></tr></tbody></table><h2 id="命令提示符键盘快捷方式" tabindex="-1">命令提示符键盘快捷方式 <a class="header-anchor" href="#命令提示符键盘快捷方式" aria-label="Permalink to &quot;命令提示符键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">功能</th></tr></thead><tbody><tr><td style="text-align:left;">Ctrl + C（或 Ctrl + Insert）</td><td style="text-align:left;">复制选定文本。</td></tr><tr><td style="text-align:left;">Ctrl + V（或 Shift + Insert）</td><td style="text-align:left;">粘贴选定文本。</td></tr><tr><td style="text-align:left;">Ctrl + M</td><td style="text-align:left;">进入标记模式。</td></tr><tr><td style="text-align:left;">Alt + 选择键</td><td style="text-align:left;">开始在块模式下选择。</td></tr><tr><td style="text-align:left;">箭头键</td><td style="text-align:left;">按指定方向移动光标。</td></tr><tr><td style="text-align:left;">Page up</td><td style="text-align:left;">将光标向上移动一个页面。</td></tr><tr><td style="text-align:left;">Page Down</td><td style="text-align:left;">将光标向下移动一个页面。</td></tr><tr><td style="text-align:left;">Ctrl + Home（标记模式）</td><td style="text-align:left;">将光标移动到缓冲区的起始处。</td></tr><tr><td style="text-align:left;">Ctrl + End（标记模式）</td><td style="text-align:left;">将光标移动到缓冲区的结尾处。</td></tr><tr><td style="text-align:left;">Ctrl + 向上键</td><td style="text-align:left;">在输出历史记录中上移一行。</td></tr><tr><td style="text-align:left;">Ctrl + 向下键</td><td style="text-align:left;">在输出历史记录中下移一行。</td></tr><tr><td style="text-align:left;">Ctrl + Home（历史记录导航）</td><td style="text-align:left;">如果命令行为空，则将视区移动到缓冲区顶部。 否则，请删除命令行中光标左侧的所有字符。</td></tr><tr><td style="text-align:left;">Ctrl + End（历史记录导航）</td><td style="text-align:left;">如果命令行为空，则将视区移动到命令行。 否则，请删除命令行中光标右侧的所有字符。</td></tr></tbody></table><h2 id="对话框键盘快捷方式" tabindex="-1">对话框键盘快捷方式 <a class="header-anchor" href="#对话框键盘快捷方式" aria-label="Permalink to &quot;对话框键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">功能</th></tr></thead><tbody><tr><td style="text-align:left;">F4</td><td style="text-align:left;">显示活动列表中的项目。</td></tr><tr><td style="text-align:left;">Ctrl + Tab</td><td style="text-align:left;">在选项卡中向前移动。</td></tr><tr><td style="text-align:left;">Ctrl + Shift + Tab</td><td style="text-align:left;">在选项卡中向后移动。</td></tr><tr><td style="text-align:left;">Ctrl + 数字 （数字 1–9）</td><td style="text-align:left;">移动到第 n 个选项卡。</td></tr><tr><td style="text-align:left;">Tab</td><td style="text-align:left;">在选项中向前移动。</td></tr><tr><td style="text-align:left;">Shift + Tab</td><td style="text-align:left;">在选项中向后移动。</td></tr><tr><td style="text-align:left;">Alt + 带下划线的字母</td><td style="text-align:left;">执行可与该字母结合使用的命令（或选择该选项）。</td></tr><tr><td style="text-align:left;">空格键</td><td style="text-align:left;">如果活动选项为复选框，则选择或清除复选框。</td></tr><tr><td style="text-align:left;">Backspace</td><td style="text-align:left;">如果在“另存为”或“打开”对话框中选择文件夹，则打开上一级别的文件夹。</td></tr><tr><td style="text-align:left;">箭头键</td><td style="text-align:left;">如果活动选项是一组选项按钮，请选择一个按钮。</td></tr></tbody></table><h2 id="文件资源管理器键盘快捷方式" tabindex="-1">文件资源管理器键盘快捷方式 <a class="header-anchor" href="#文件资源管理器键盘快捷方式" aria-label="Permalink to &quot;文件资源管理器键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">功能</th></tr></thead><tbody><tr><td style="text-align:left;">Alt + D</td><td style="text-align:left;">选择地址栏。</td></tr><tr><td style="text-align:left;">Ctrl + E</td><td style="text-align:left;">选择搜索框。</td></tr><tr><td style="text-align:left;">Ctrl + F</td><td style="text-align:left;">选择搜索框。</td></tr><tr><td style="text-align:left;">Ctrl + N</td><td style="text-align:left;">打开新窗口。</td></tr><tr><td style="text-align:left;">Ctrl + W</td><td style="text-align:left;">关闭活动窗口。</td></tr><tr><td style="text-align:left;">Ctrl + 鼠标滚轮</td><td style="text-align:left;">更改文件和文件夹图标的大小和外观。</td></tr><tr><td style="text-align:left;">Ctrl + Shift + E</td><td style="text-align:left;">显示选定文件夹上的所有文件夹。</td></tr><tr><td style="text-align:left;">Ctrl + Shift + N</td><td style="text-align:left;">创建新文件夹。</td></tr><tr><td style="text-align:left;">Num Lock + 星号 (*)</td><td style="text-align:left;">显示选定文件夹下的所有子文件夹。</td></tr><tr><td style="text-align:left;">Num Lock + 加号 (+)</td><td style="text-align:left;">显示选定文件夹中的内容。</td></tr><tr><td style="text-align:left;">Num Lock + 减号 (-)</td><td style="text-align:left;">折叠选定文件夹。</td></tr><tr><td style="text-align:left;">Alt + P</td><td style="text-align:left;">显示预览面板。</td></tr><tr><td style="text-align:left;">Alt + Enter</td><td style="text-align:left;">打开选定项的“属性”对话框。</td></tr><tr><td style="text-align:left;">Alt + 向右键</td><td style="text-align:left;">查看下一个文件夹。</td></tr><tr><td style="text-align:left;">Alt + 向上键</td><td style="text-align:left;">查看该文件夹所在的文件夹。</td></tr><tr><td style="text-align:left;">Alt + 向左键</td><td style="text-align:left;">查看上一个文件夹。</td></tr><tr><td style="text-align:left;">Backspace</td><td style="text-align:left;">查看上一个文件夹。</td></tr><tr><td style="text-align:left;">向右键</td><td style="text-align:left;">显示当前选择内容（如果已折叠），或选择第一个子文件夹。</td></tr><tr><td style="text-align:left;">向左键</td><td style="text-align:left;">折叠当前选择内容（如果已展开），或选择该文件夹所在的文件夹。</td></tr><tr><td style="text-align:left;">End</td><td style="text-align:left;">显示活动窗口底部。</td></tr><tr><td style="text-align:left;">Home 键</td><td style="text-align:left;">显示活动窗口顶部。</td></tr><tr><td style="text-align:left;">F11</td><td style="text-align:left;">最大化或最小化活动窗口。</td></tr></tbody></table><h2 id="虚拟桌面的键盘快捷方式" tabindex="-1">虚拟桌面的键盘快捷方式 <a class="header-anchor" href="#虚拟桌面的键盘快捷方式" aria-label="Permalink to &quot;虚拟桌面的键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:center;">快捷键</th><th style="text-align:center;">功能</th></tr></thead><tbody><tr><td style="text-align:center;">Win + Tab</td><td style="text-align:center;">打开任务视图。</td></tr><tr><td style="text-align:center;">Win + Ctrl + D</td><td style="text-align:center;">添加虚拟桌面。</td></tr><tr><td style="text-align:center;">Win + Ctrl + 向右键</td><td style="text-align:center;">在你于右侧创建的虚拟桌面之间切换。</td></tr><tr><td style="text-align:center;">Win + Ctrl + 向左键</td><td style="text-align:center;">在你于左侧创建的虚拟桌面之间切换。</td></tr><tr><td style="text-align:center;">Win + Ctrl + F4</td><td style="text-align:center;">关闭你正在使用的虚拟桌面。</td></tr></tbody></table><h2 id="任务栏键盘快捷方式" tabindex="-1">任务栏键盘快捷方式 <a class="header-anchor" href="#任务栏键盘快捷方式" aria-label="Permalink to &quot;任务栏键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">功能</th></tr></thead><tbody><tr><td style="text-align:left;">Shift + 单击任务栏按钮</td><td style="text-align:left;">打开应用或快速打开另一个应用实例。</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 单击任务栏按钮</td><td style="text-align:left;">以管理员身份打开应用。</td></tr><tr><td style="text-align:left;">Shift + 右键单击任务栏按钮</td><td style="text-align:left;">显示应用的窗口菜单。</td></tr><tr><td style="text-align:left;">Shift + 右键单击分组任务栏按钮</td><td style="text-align:left;">显示组的窗口菜单。</td></tr><tr><td style="text-align:left;">Ctrl + 单击分组任务栏按钮</td><td style="text-align:left;">循环浏览组的窗口。</td></tr></tbody></table><h2 id="设置键盘快捷方式" tabindex="-1">设置键盘快捷方式 <a class="header-anchor" href="#设置键盘快捷方式" aria-label="Permalink to &quot;设置键盘快捷方式&quot;">​</a></h2><table><thead><tr><th style="text-align:center;">快捷键</th><th style="text-align:center;">功能</th></tr></thead><tbody><tr><td style="text-align:center;">Win + I</td><td style="text-align:center;">打开设置。</td></tr><tr><td style="text-align:center;">Backspace</td><td style="text-align:center;">返回到设置主页。</td></tr><tr><td style="text-align:center;">使用搜索框在任何页面上键入</td><td style="text-align:center;">搜索设置。</td></tr></tbody></table>',17),a=[n];function i(s,f,y,x,g,h){return d(),e("div",null,a)}const c=t(l,[["render",i]]);export{W as __pageData,c as default};
