import{_ as a,o as s,c as l,Q as n}from"./chunks/framework.aaef0e9b.js";const y=JSON.parse('{"title":"adb","description":"","frontmatter":{},"headers":[],"relativePath":"other/自动化测试/abd.md","lastUpdated":1693385324000}'),e={name:"other/自动化测试/abd.md"},p=n(`<h1 id="adb" tabindex="-1">adb <a class="header-anchor" href="#adb" aria-label="Permalink to &quot;adb&quot;">​</a></h1><h2 id="adb是什么" tabindex="-1">ADB是什么？ <a class="header-anchor" href="#adb是什么" aria-label="Permalink to &quot;ADB是什么？&quot;">​</a></h2><ul><li>adb（Android Debug Bridge）是android sdk的一个工具，即调试桥，方便调试设备或调试开发的Android APP;</li><li>adb是用来连接安卓手机和PC端的桥梁，要有adb作为二者之间的维系，才能让用户在电脑上对手机进行全面的操作;（用这个工具可以直接操作管理android模拟器或者真实的android设备）</li><li>Android的初衷是用adb这样的一个工具来协助开发人员在开发android应用的过程中更快更好的调试apk，因此adb具有安装卸载apk、拷贝推送文件、查看设备硬件信息、查看应用程序占用资源、在设备执行shell命令等功能。</li><li>你可以在Android SDK/platform-tools中找到 adb 工具或下载ADB。</li></ul><h2 id="adb三大组件" tabindex="-1">adb三大组件 <a class="header-anchor" href="#adb三大组件" aria-label="Permalink to &quot;adb三大组件&quot;">​</a></h2><h3 id="客户端" tabindex="-1">客户端 <a class="header-anchor" href="#客户端" aria-label="Permalink to &quot;客户端&quot;">​</a></h3><p>该组件发送命令，客户端在开发计算机上运行，可以通过发出 adb 命令从命令行终端调用客户端。</p><h3 id="后台程序" tabindex="-1">后台程序 <a class="header-anchor" href="#后台程序" aria-label="Permalink to &quot;后台程序&quot;">​</a></h3><p>该组件在设备上运行命令，后台程序在每个模拟器或设备实例上作为后台进程运行。</p><h3 id="服务器" tabindex="-1">服务器 <a class="header-anchor" href="#服务器" aria-label="Permalink to &quot;服务器&quot;">​</a></h3><p>该组件管理客户端和后台程序之间的通信，服务器在开发计算机上作为后台进程运行。</p><h2 id="adb环境搭建" tabindex="-1">ADB环境搭建 <a class="header-anchor" href="#adb环境搭建" aria-label="Permalink to &quot;ADB环境搭建&quot;">​</a></h2><h3 id="搭建java环境" tabindex="-1">搭建java环境 <a class="header-anchor" href="#搭建java环境" aria-label="Permalink to &quot;搭建java环境&quot;">​</a></h3><h3 id="安装android-sdk" tabindex="-1">安装Android sdk <a class="header-anchor" href="#安装android-sdk" aria-label="Permalink to &quot;安装Android sdk&quot;">​</a></h3><p><a href="https://developer.android.com" target="_blank" rel="noreferrer">https://developer.android.com</a></p><p><a href="https://developer.android.google.cn" target="_blank" rel="noreferrer">https://developer.android.google.cn</a></p><h3 id="设置环境变量" tabindex="-1">设置环境变量 <a class="header-anchor" href="#设置环境变量" aria-label="Permalink to &quot;设置环境变量&quot;">​</a></h3><h3 id="验证环境-adb-version" tabindex="-1">验证环境 adb version <a class="header-anchor" href="#验证环境-adb-version" aria-label="Permalink to &quot;验证环境 adb version&quot;">​</a></h3><h2 id="常用命令" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令" aria-label="Permalink to &quot;常用命令&quot;">​</a></h2><h3 id="安装apk" tabindex="-1">安装apk <a class="header-anchor" href="#安装apk" aria-label="Permalink to &quot;安装apk&quot;">​</a></h3><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">adb install D:\\ruanjian\\apps\\dgms.apk</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><h3 id="列出包名" tabindex="-1">列出包名 <a class="header-anchor" href="#列出包名" aria-label="Permalink to &quot;列出包名&quot;">​</a></h3><ul><li>进入手机命令行界面</li></ul><div class="language-cmd"><button title="Copy Code" class="copy"></button><span class="lang">cmd</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#BABED8;">adb shell</span></span>
<span class="line"><span style="color:#BABED8;"># adb </span><span style="color:#89DDFF;">-</span><span style="color:#BABED8;">s 指定设备 进行连接</span></span>
<span class="line"><span style="color:#BABED8;">adb </span><span style="color:#89DDFF;">-</span><span style="color:#BABED8;">s </span><span style="color:#F78C6C;">127.0.0.1</span><span style="color:#BABED8;">:</span><span style="color:#F78C6C;">62001</span><span style="color:#BABED8;"> shell</span></span>
<span class="line"></span></code></pre></div><ul><li>列出所有包名称</li></ul><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">/data/data</span></span>
<span class="line"><span style="color:#FFCB6B;">ls</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 或者</span></span>
<span class="line"><span style="color:#FFCB6B;">ll</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 或者</span></span>
<span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">|</span><span style="color:#BABED8;"> </span><span style="color:#FFCB6B;">grep</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">PackageName</span></span>
<span class="line"></span></code></pre></div><ul><li>列出所有包名</li></ul><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 所有应用的包名列表</span></span>
<span class="line"><span style="color:#FFCB6B;">adb</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">shell</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">pm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">list</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">packages</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 第三方应用包名列表</span></span>
<span class="line"><span style="color:#FFCB6B;">adb</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">shell</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">pm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">list</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">packages</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-3</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 系统应用包名列表</span></span>
<span class="line"><span style="color:#FFCB6B;">adb</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">shell</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">pm</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">list</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">packages</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-s</span><span style="color:#BABED8;"> </span></span>
<span class="line"></span></code></pre></div><ul><li>正在运行应用包名</li></ul><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">adb</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">shell</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">dumpsys</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">window</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">|</span><span style="color:#BABED8;"> </span><span style="color:#FFCB6B;">findstr</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">mCurrentFocus</span></span>
<span class="line"></span></code></pre></div><ul><li>卸载 app</li></ul><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">adb shell pm uninstall --user 0 &lt;packages&gt;</span></span>
<span class="line"><span style="color:#babed8;">adb shell pm uninstall --user 0 com.douguo.recipe</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><ul><li>运行 app</li></ul><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">adb shell monkey -p 包名 -v -v -v 1 | findstr &quot;cmp=&quot;</span></span>
<span class="line"><span style="color:#babed8;"># 先找到app的启动页</span></span>
<span class="line"><span style="color:#babed8;">adb shell monkey -p com.douguo.recipe -v -v -v 1 | findstr &quot;cmp=&quot;</span></span>
<span class="line"><span style="color:#babed8;"># com.douguo.recipe/.MainActivity</span></span>
<span class="line"><span style="color:#babed8;"></span></span>
<span class="line"><span style="color:#babed8;">#键入activity启动应用（格式为包名 + activity）</span></span>
<span class="line"><span style="color:#babed8;">adb shell am start com.douguo.recipe/.MainActivity</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><ul><li>参考</li></ul><p><a href="https://blog.csdn.net/m0_46151559/article/details/126461868" target="_blank" rel="noreferrer">使用adb命令启动app</a></p><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><p><a href="https://blog.csdn.net/weixin_54280625/article/details/112862918?ops_request_misc=%257B%2522request%255Fid%2522%253A%2522168618396716800225551728%2522%252C%2522scm%2522%253A%252220140713.130102334..%2522%257D&amp;request_id=168618396716800225551728&amp;biz_id=0&amp;utm_medium=distribute.pc_search_result.none-task-blog-2~all~top_positive~default-2-112862918-null-null.142%5Ev88%5Ekoosearch_v1,239%5Ev2%5Einsert_chatgpt&amp;utm_term=adb&amp;spm=1018.2226.3001.4187" target="_blank" rel="noreferrer">adb概介</a></p><p><a href="https://blog.csdn.net/lb245557472/article/details/84068519?ops_request_misc=%257B%2522request%255Fid%2522%253A%2522168618396716800225551728%2522%252C%2522scm%2522%253A%252220140713.130102334..%2522%257D&amp;request_id=168618396716800225551728&amp;biz_id=0&amp;utm_medium=distribute.pc_search_result.none-task-blog-2~all~top_positive~default-1-84068519-null-null.142%5Ev88%5Ekoosearch_v1,239%5Ev2%5Einsert_chatgpt&amp;utm_term=adb&amp;spm=1018.2226.3001.4187" target="_blank" rel="noreferrer">ADB 操作命令详解及用法大全</a></p><p><a href="https://blog.csdn.net/qq_40558166/article/details/124497404" target="_blank" rel="noreferrer">adb连接夜神模拟器和连接夜神多开的方法</a></p><p><a href="http://www.360doc.com/content/21/1028/11/77509131_1001637303.shtml" target="_blank" rel="noreferrer">自动化 | 聊聊 ADB</a></p><p><a href="https://blog.csdn.net/qq_42069296/article/details/128660317?utm_medium=distribute.pc_relevant.none-task-blog-2~default~baidujs_baidulandingword~default-1-128660317-blog-122584545.235%5Ev38%5Epc_relevant_anti_t3_base&amp;spm=1001.2101.3001.4242.2&amp;utm_relevant_index=4" target="_blank" rel="noreferrer">Android adb 常用命令</a></p>`,41),o=[p];function t(r,c,i,d,h,b){return s(),l("div",null,o)}const m=a(e,[["render",t]]);export{y as __pageData,m as default};
