import{_ as a,o as n,c as s,Q as e}from"./chunks/framework.aaef0e9b.js";const C=JSON.parse('{"title":"在 Ubuntu 中配置 Conda 环境","description":"","frontmatter":{},"headers":[],"relativePath":"server/python/在Ubuntu中配置Conda环境.md","lastUpdated":1712715664000}'),o={name:"server/python/在Ubuntu中配置Conda环境.md"},l=e(`<h1 id="在-ubuntu-中配置-conda-环境" tabindex="-1">在 Ubuntu 中配置 Conda 环境 <a class="header-anchor" href="#在-ubuntu-中配置-conda-环境" aria-label="Permalink to &quot;在 Ubuntu 中配置 Conda 环境&quot;">​</a></h1><p>Conda 是一个开源的包管理器和环境管理器，可以用于安装、运行和更新包和环境。它非常适用于管理多个数据科学、Python项目，确保各个项目的依赖隔离，避免相互冲突。</p><h2 id="下载-anaconda-安装器" tabindex="-1">下载 Anaconda 安装器 <a class="header-anchor" href="#下载-anaconda-安装器" aria-label="Permalink to &quot;下载 Anaconda 安装器&quot;">​</a></h2><p>下载地址：</p><p><a href="https://www.anaconda.com/download#downloads" target="_blank" rel="noreferrer">https://www.anaconda.com/download#downloads</a></p><p><a href="https://repo.anaconda.com/archive/Anaconda3-2024.02-1-Linux-x86_64.sh" target="_blank" rel="noreferrer">https://repo.anaconda.com/archive/Anaconda3-2024.02-1-Linux-x86_64.sh</a></p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">wget</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">https://repo.anaconda.com/archive/Anaconda3-2024.02-1-Linux-x86_64.sh</span></span>
<span class="line"></span></code></pre></div><h2 id="终端中安装-anaconda" tabindex="-1">终端中安装 Anaconda <a class="header-anchor" href="#终端中安装-anaconda" aria-label="Permalink to &quot;终端中安装 Anaconda&quot;">​</a></h2><h3 id="安装软件" tabindex="-1">安装软件 <a class="header-anchor" href="#安装软件" aria-label="Permalink to &quot;安装软件&quot;">​</a></h3><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">Anaconda3-2024.02-1-Linux-x86_64.sh</span></span>
<span class="line"></span></code></pre></div><h3 id="接受许可协议" tabindex="-1">接受许可协议 <a class="header-anchor" href="#接受许可协议" aria-label="Permalink to &quot;接受许可协议&quot;">​</a></h3><p>输入 <code>yes</code></p><h3 id="选择安装位置" tabindex="-1">选择安装位置 <a class="header-anchor" href="#选择安装位置" aria-label="Permalink to &quot;选择安装位置&quot;">​</a></h3><p>默认位置 <code>/home/user/anaconda3</code></p><h3 id="初始化-conda" tabindex="-1">初始化 Conda <a class="header-anchor" href="#初始化-conda" aria-label="Permalink to &quot;初始化 Conda&quot;">​</a></h3><p>在安装过程的最后，脚本会询问你是否希望让 <code>conda init</code> 初始化。建议输入 <code>yes</code> ，这将使 Conda 集成到你的 shell 脚本中，允许你直接从终端激活 Conda 环境。</p><h2 id="激活安装" tabindex="-1">激活安装 <a class="header-anchor" href="#激活安装" aria-label="Permalink to &quot;激活安装&quot;">​</a></h2><p>安装完成后，为了使安装生效，你需要关闭并重新打开终端窗口，或者运行以下命令来激活安装环境：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">source</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">~/.bashrc</span></span>
<span class="line"></span></code></pre></div><h2 id="测试-conda-安装" tabindex="-1">测试 Conda 安装 <a class="header-anchor" href="#测试-conda-安装" aria-label="Permalink to &quot;测试 Conda 安装&quot;">​</a></h2><p>通过运行以下命令来测试 Conda 是否正确安装：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">conda</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">list</span></span>
<span class="line"></span></code></pre></div><p>如果安装成功，你将看到已安装的包列表。</p><h2 id="创建和激活新的-conda-环境" tabindex="-1">创建和激活新的 Conda 环境 <a class="header-anchor" href="#创建和激活新的-conda-环境" aria-label="Permalink to &quot;创建和激活新的 Conda 环境&quot;">​</a></h2><p>通过以下命令创建一个新的 Conda 环境，其中 <code>myenv</code> 是环境名称，<code>python=3.8</code> 指定了 Python 版本：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">conda</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">create</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">--name</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">myenv</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">python=</span><span style="color:#F78C6C;">3.8</span></span>
<span class="line"></span></code></pre></div><p>创建环境后，使用以下命令激活该环境：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">conda</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">activate</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">myenv</span></span>
<span class="line"></span></code></pre></div><h2 id="管理包" tabindex="-1">管理包 <a class="header-anchor" href="#管理包" aria-label="Permalink to &quot;管理包&quot;">​</a></h2><p>在 Conda 环境中，可以使用 <code>conda install</code> 命令安装新的包。例如，要安装 <code>NumPy</code> ，可以使用：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">conda</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">install</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">numpy</span></span>
<span class="line"></span></code></pre></div><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><p><a href="https://www.8kiz.cn/archives/17534.html" target="_blank" rel="noreferrer">在Ubuntu中配置Conda环境的步骤</a></p>`,33),t=[l];function c(p,r,d,i,h,u){return n(),s("div",null,t)}const b=a(o,[["render",c]]);export{C as __pageData,b as default};
