import{_ as a,o as e,c as s,Q as n}from"./chunks/framework.aaef0e9b.js";const u=JSON.parse('{"title":"pyinstaller - python 脚本打包成 exe","description":"","frontmatter":{},"headers":[],"relativePath":"server/python/模块/pyinstaller.md","lastUpdated":1686565809000}'),l={name:"server/python/模块/pyinstaller.md"},t=n(`<h1 id="pyinstaller-python-脚本打包成-exe" tabindex="-1">pyinstaller - python 脚本打包成 exe <a class="header-anchor" href="#pyinstaller-python-脚本打包成-exe" aria-label="Permalink to &quot;pyinstaller - python 脚本打包成 exe&quot;">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">pip install pyinstaller</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><h2 id="打包单文件" tabindex="-1">打包单文件 <a class="header-anchor" href="#打包单文件" aria-label="Permalink to &quot;打包单文件&quot;">​</a></h2><p>进入 <code>a.py</code> 文件所在目录下执行：</p><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">Pyinstaller</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-F</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">a.py</span></span>
<span class="line"></span></code></pre></div><p>在 <code>a.py</code> 同级目录下，生成 <code>dist/a.exe</code></p><ul><li>相关命令</li></ul><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;"># 打包exe</span></span>
<span class="line"><span style="color:#babed8;">Pyinstaller -F a.py </span></span>
<span class="line"><span style="color:#babed8;"># 不带控制台的打包</span></span>
<span class="line"><span style="color:#babed8;">Pyinstaller -F -w a.py </span></span>
<span class="line"><span style="color:#babed8;"># 打包指定exe图标打包 tb是图标文件名</span></span>
<span class="line"><span style="color:#babed8;">Pyinstaller -F -w -i tb.ico a.py </span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre></div><p>这里就给大家分享一个网站，可以把其他格式图片转成ico格式：<a href="https://app.xunjiepdf.com/img2icon/" target="_blank" rel="noreferrer">https://app.xunjiepdf.com/img2icon/</a></p><h2 id="打包多文件" tabindex="-1">打包多文件 <a class="header-anchor" href="#打包多文件" aria-label="Permalink to &quot;打包多文件&quot;">​</a></h2><p>todo 待完善</p><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://blog.csdn.net/WWWQQQEEECCC/article/details/127487095" target="_blank" rel="noreferrer">【Python】如何将写好的Python代码打包成exe文件？</a></li><li><a href="https://zhuanlan.zhihu.com/p/444647750" target="_blank" rel="noreferrer">Python程序打包成exe</a></li><li><a href="http://www.360doc.com/content/23/0530/15/541242_1082728808.shtml" target="_blank" rel="noreferrer">python打包exe最清楚的教程来了</a></li></ul>`,14),p=[t];function o(r,i,c,d,h,y){return e(),s("div",null,p)}const _=a(l,[["render",o]]);export{u as __pageData,_ as default};
