import{_ as a,o as e,c as t,Q as o}from"./chunks/framework.aaef0e9b.js";const b=JSON.parse('{"title":"韩尚优品综合平台","description":"","frontmatter":{},"headers":[],"relativePath":"问答/项目总结/韩尚优品综合平台.md","lastUpdated":1683280071000}'),r={name:"问答/项目总结/韩尚优品综合平台.md"},s=o('<h1 id="韩尚优品综合平台" tabindex="-1">韩尚优品综合平台 <a class="header-anchor" href="#韩尚优品综合平台" aria-label="Permalink to &quot;韩尚优品综合平台&quot;">​</a></h1><h2 id="项目描述-boss-直聘" tabindex="-1">项目描述 -- boss 直聘 <a class="header-anchor" href="#项目描述-boss-直聘" aria-label="Permalink to &quot;项目描述 -- boss 直聘&quot;">​</a></h2><p>项目概况 韩尚优品是一家快时尚连锁百货零售企业，全球拥有1000多家实体加盟门店。该商城主要功能： 1 帮助国内实体加盟门店进行商品铺货； 2 线上商城与线下实体店相结合，帮助门店进行锁客、拓新等会员管理功能，有效提升复购率； 3 后端线上商城与线下门店及ERP的数据互通，实现从供应链到C端用户的数据自动化，方便总部实时掌握门店商品、库存、销量、营业额以及会员动态，并形成综合热销品类、季节热销品类、区域热销品类、活跃会员年龄与购买习惯、总部大区与单店各项数据指标等综合性数据分析系统；</p><p>我的工作： 1 参与项目流程研讨制定； 2 负责项目技术选型及架构搭建； 3 负责后端核心业务流程的开发实现； 4 负责微信公众号小程序、直播、ERP系统、银行微信支付宝支付平台等对接开发； 5 负责项目各项性能优化测试； 6 负责项目部署等服务器操作； 7 协助前端进行 APP、微信小程序的开发； 8 根据市场门店与会员使用反馈，对系统持续升级迭代； 9 协调开发团队工作，对项目总体进度负直接责任；</p><h2 id="项目架构" tabindex="-1">项目架构 <a class="header-anchor" href="#项目架构" aria-label="Permalink to &quot;项目架构&quot;">​</a></h2><p>● 韩尚优品综合平台是一个以商城为主的购物平台， ● 使用 Laravel+MySQL+Redis 在 Docker 容器中部署； ● 以微服务的形式部署项目：商城商品订单支付，数据统计，erp，售后，客服，社区，消息中心 等服务； ● 图片视频等资源服务，商学院课程服务。 ● MySQL读写分离</p><h2 id="项目技术点" tabindex="-1">项目技术点 <a class="header-anchor" href="#项目技术点" aria-label="Permalink to &quot;项目技术点&quot;">​</a></h2><ul><li>laravel 框架，涉及 ICO 容器</li><li>订单使用 Redis 队列</li><li>docker 部署项目，微服务架构，使用 redis 统一管理 session</li><li></li></ul>',8),i=[s];function l(_,n,d,c,h,p){return e(),t("div",null,i)}const m=a(r,[["render",l]]);export{b as __pageData,m as default};
