import{_ as a,c as e,o as s,a5 as t}from"./chunks/framework.DPGmCwCP.js";const g=JSON.parse('{"title":"GitHub 添加 SSH 秘钥","description":"","frontmatter":{},"headers":[],"relativePath":"Git/添加SSH秘钥-github.md","filePath":"Git/添加SSH秘钥-github.md","lastUpdated":null}'),i={name:"Git/添加SSH秘钥-github.md"},n=t('<h1 id="github-添加-ssh-秘钥" tabindex="-1">GitHub 添加 SSH 秘钥 <a class="header-anchor" href="#github-添加-ssh-秘钥" aria-label="Permalink to &quot;GitHub 添加 SSH 秘钥&quot;">​</a></h1><h2 id="生成-添加ssh公钥-本地" tabindex="-1">生成/添加SSH公钥(本地) <a class="header-anchor" href="#生成-添加ssh公钥-本地" aria-label="Permalink to &quot;生成/添加SSH公钥(本地)&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ssh-keygen -t ed25519 -C &quot;your_email@example.com&quot;</span></span>\n<span class="line"><span># 然后一路回车即可</span></span></code></pre></div><h2 id="查看-ssh-id-rsa-pub-文件内容-获取公钥-public-key" tabindex="-1">查看 <code>~/.ssh/id_rsa.pub</code> 文件内容，获取公钥 public key <a class="header-anchor" href="#查看-ssh-id-rsa-pub-文件内容-获取公钥-public-key" aria-label="Permalink to &quot;查看 `~/.ssh/id_rsa.pub` 文件内容，获取公钥 public key&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span> cat ~/.ssh/id_ed25519.pub</span></span></code></pre></div><blockquote><p>大概长这样：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ssh-ed25519 AAAAC3NzaC1lZDI1NTE5AAA...</span></span></code></pre></div></blockquote><h2 id="复制生成的公钥-添加到-gitee-仓库中" tabindex="-1">复制生成的公钥，添加到 gitee 仓库中 <a class="header-anchor" href="#复制生成的公钥-添加到-gitee-仓库中" aria-label="Permalink to &quot;复制生成的公钥，添加到 gitee 仓库中&quot;">​</a></h2><ul><li>路径：仓库主页 「管理」-&gt;「部署公钥管理」-&gt;「添加部署公钥」</li></ul><h2 id="添加后-在终端-terminal-中输入" tabindex="-1">添加后，在终端（Terminal）中输入 <a class="header-anchor" href="#添加后-在终端-terminal-中输入" aria-label="Permalink to &quot;添加后，在终端（Terminal）中输入&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ssh -T git@github.com</span></span></code></pre></div><p>报错参考：</p><p><a href="https://blog.csdn.net/qq_41884002/article/details/123358315" target="_blank" rel="noreferrer">WARNING: REMOTE HOST IDENTIFICATION HAS CHANGED</a></p><h2 id="更换仓库地址-【https-ssh】" tabindex="-1">更换仓库地址 【https =&gt; ssh】 <a class="header-anchor" href="#更换仓库地址-【https-ssh】" aria-label="Permalink to &quot;更换仓库地址 【https =&gt; ssh】&quot;">​</a></h2><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>git remote set-url origin https://gitee.com/xx/xx.git (新地址 -- 更新)</span></span></code></pre></div>',14),l=[n];function p(o,h,c,d,r,u){return s(),e("div",null,l)}const _=a(i,[["render",p]]);export{g as __pageData,_ as default};
